<?php    class checkAuth{        public static function userAuth($user_login, $user_password) {            if($user_login == $_POST['login'] && $user_password == $_POST['password']){                $user = userByLogin::userAuth(mb_strtolower($user_login));                if($user == null){                    echo "Неверная авторизация";                    return null;                }                $user_object = new user($user);                return $user_object;            } else {                return null;            }        }    }