<?php    class userByLogin{        public static function userAuth($str_login) {            $str_path = 'users/'.$str_login.'.txt';            if (file_exists($str_path)) {                $file = file_get_contents($str_path, FILE_USE_INCLUDE_PATH);                if($file == true){                    $user = json_decode($file);                    if(isset($user)){                        return $user;                    } else {                        return null;                    }                } else {                    echo 'Пользователь отсутствует';                    return null;                }            } else {                return null;            }        }    }